            }
        }
        developers {
            developer {
                id = "saths008"
                name = "Saath Satheeshkumar"
                url = "https://github.com/saths008"
            }
        }
        scm {
            url = "https://github.com/saths008/dropwizard-kodein/"
            connection = "scm:git:git://github.com/saths008/dropwizard-kodein.git"
